mapper.insert:
/*.Cm0101DaoinsertBoard[게시판 등록]*/
INSERT INTO TB_BOARD
(
     IDX
    ,PARENT_IDX
    ,TITLE
    ,CONTENTS
    ,UPD_DT
)VALUES(
     (SELECT MAX(IDX)+1 FROM TB_BOARD)
    ,(SELECT MAX(IDX)+1 FROM TB_BOARD)
    ,#{title}
    ,#{contents}
    ,TO_CHAR(sysdate,'YYYY-MM-DD')
)

mapper.update:
/*Cm0101Dao.updateBoard [게시판 수정]*/
UPDATE TB_BOARD SET TITLE = #{title}
                  , CONTENTS= #{contents}
                  , UPD_DT = TO_CHAR(sysdate,'YYYY-MM-DD')
 WHERE IDX=#{idx}

mapper.delete:
/*Cm0101Dao.deleteBoard [게시판 삭제]*/
DELETE 
  FROM TB_BOARD 
 WHERE IDX=#{idx}

mapper.select:
/*Cm0101Dao.selectUser [사용자 조회]*/
SELECT MBR_ID
     , MBR_NM
  FROM MB_MBR_B
 WHERE MBR_EML_ADDR = #{mbrEmlAddr}
   AND USER_PSWD     = #{userPswd}
   AND USE_YN       = 'Y' 
   AND ROWNUM       = 1
